import { Injectable } from "@angular/core";
import { Actions } from "@ngrx/effects";
 import { PostService } from "src/app/Services/Post.service";
import { invokePostApi } from "../Actions/Posts.Action";
import { map, switchMap } from "rxjs";

@Injectable()
export class PostEffect {
  constructor(private action$ :Actions,private postService:PostService)
  {

  }
  @Effect()
  getPosts$=this.action$.pipe(ofType(invokePostApi),switchMap(()=>
    this.postService.GetPosts().pipe(map((data)=>({type:'[Post API]  Post API Success',posts:data})))
  ))

}
