import { Component, OnInit } from '@angular/core';
import { Store, select } from '@ngrx/store';
import { Observable } from 'rxjs';
import { getPosts } from 'src/app/AppStore/Actions/Posts.Action';
import { Post } from 'src/app/Models/Post';
import { PostService } from 'src/app/Services/Post.service';

@Component({
  selector: 'app-Post',
  templateUrl: './Post.component.html',
  styleUrls: ['./Post.component.css']
})
export class PostComponent implements OnInit {
  Posts:Observable<Post[]>;
  constructor(private store:Store<{Posts:Post[]}> ,private postService:PostService ) {

    // this.Posts=this.store.pipe(select('posts'));
    this.Posts=this.store.pipe(select('customers'));

  }
   ngOnInit() {
    debugger
    this.postService.GetPosts().subscribe(res=>{

      this.store.dispatch(getPosts({posts:res as Post[]}))
    })
  }

}
<h1>Posts</h1>

<div *ngFor="let item of  this.Posts|async">
  <span>"userId:" {{item.userId}}</span>
  <span>"id:" {{item.id}}</span>
  <span>"title:" {{item.title}}</span>
  <span>"body:" {{item.body}}</span>
</div>
